package A1_기본이론;

/**
 * @author Administrator
 *
 */
public class A1_기본이론5_자료형 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 자료형 (1.숫자, 2.문자)
		// 숫자 ==> 소수점이 있는숫자, 소수점이 없는숫자.
		System.out.println(10);
		System.out.println(10.123123);
		// 문자 ==> 한글자, 여러글자
		
		// 한글자 ==> '' 홀따움표
		System.out.println('a');
		
		// 여러글자 => "" 쌍따옴표
		System.out.println("ㅁㅁㅁㅁㅁ");
		
		// 소수점이 없는숫자 ==> 정수 
		// 소수점이 있는숫자 ==> 실수
		// 한글자 ==> 캐릭터 Char
		// 여러글자 ==> String 스트링
		// 참거짓 
		System.out.println(true); // 1
		System.out.println(false); // 0
	}

}
